changeSet.sql:
alter table if exists app_user add column if not exists user_full_name varchar not null default '', add column if not exists date_of_birth date, add column if not exists address varchar, add column if not exists phone varchar, add column if not exists email varchar, add column if not exists instagram_account varchar, add column if not exists profile_image bytea;
update app_user set user_full_name = (user_last_name || ' ' || user_first_name) where user_last_name is not null;
alter table if exists app_user drop column if exists user_last_name, drop column if exists user_first_name;
